import React, { useState } from 'react'
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';

import './NavBar.scss'

const instagram = "https://www.instagram.com/pasquale.demarinis/"
const facebook = "https://www.facebook.com/Pasquale.De.Marinis.LOL"
const linkedin = "https://www.linkedin.com/in/pasquale-de-marinis-2873071a2/"
const github = "https://github.com/pasqualedem"
const twitter = "https://twitter.com/MarinisPasquale"


export default function NavBar() {
  const [color, setColor] = useState(false)
  const changeColor = () => {
    if (window.scrollY >= 90) {
      setColor(true)
    } else {
      setColor(false)
    }
  }

  window.addEventListener('scroll', changeColor)

  return (
    <Navbar collapseOnSelect bg={color ? "flat" : 'dark'} variant={color ? "flat" : 'dark'} expand="md" sticky="top">
      <Container>
        <Navbar.Brand class="navItem" href="#"><span class="navItem">Pasquale De Marinis</span></Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="me-auto" variant='flat'>
            <Nav.Link variant='flat' href="#aboutme"><span class="navItem">About me</span></Nav.Link>
            <Nav.Link variant='flat' href="#projects"><span class="navItem">Projects</span></Nav.Link>
          </Nav>
          <Nav.Link href={instagram} className="border-left pl-2 ml-auto"><i class="bi-instagram navicon"></i></Nav.Link>
          <Nav.Link href={facebook} className="border-left pl-2 ml-auto"><i class="bi-facebook navicon"></i></Nav.Link>
          <Nav.Link href={linkedin} className="border-left pl-2 ml-auto"><i class="bi-linkedin navicon"></i></Nav.Link>
          <Nav.Link href={github} className="border-left pl-2 ml-auto"><i class="bi-github navicon"></i></Nav.Link>
          <Nav.Link href={twitter} className="border-left pl-2 ml-auto"><i class="bi-twitter navicon"></i></Nav.Link>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  )
}
