import React from 'react'

import Hobbies from './Hobbies.js'
import Language from './Language.js'

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image'

import './SideColumn.css'

const hobbies = [
  "bicycle",
  "controller"
]

const languages = [
  {'name': 'Italian', 'level': 'Native'},
  {'name': 'Enlglish', 'level': 'Expert'},
]

const email = "pas.demarinis@gmail.com"

export default function SideColumn() {
  return (
    <Col lg={{ span: 4 }} md="4" sm="4">
    <div class="imgContainer">
    <Image thumbnail={true} roundedCircle={true} fluid={true} src={require("../asset/images/photo.jpg")}/>
    </div>

    <Container>
      <Row>
        <span> <i class="bi-envelope email"></i><a href={email}>{email}</a></span>
        <div class="hobbies">
          <h3 class="h3-hobbies"> Hobbies </h3>
          <Hobbies hobbies={hobbies} />
        </div>
          <h3> Languages </h3>
          {languages.map(
            language => {
            return <Language language={language} />
            })}
        </Row>
    </Container>

  </Col>
  )
}
